/* eslint-disable no-undef */
import React, { Component } from "react";
import FighterCard from "./components/FighterCard/FighterCards";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import fighters from "./fighters.json";
// import ScoreCard from "./components/ScoreCard/ScoreBoard"

function randomFighters(array) {
  for (let i = array.length - 1; i > 0; i--) {
    let j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
};

class App extends Component {
  // state = {
  //   fighters: fighters,
  //   score: 0,
  //   winScore: 12,
  //   message: "Click on your fighters to start but, don't click the same fighters more than once! Good luck!"
  // };

  setClicked = () => {
    const shuffled = randomFighters(fighters);
    this.setState({ fighters: shuffled });
  }

  constructor(props) {
    super(props);
    this.state = { score: 0 }
  }

  increaseScore() {
    this.setState({
      score: this.state.score + 1
    });
  }

  render() {
    return (

      <Wrapper>
        <h2>{this.props.name}</h2>
        <h1>{this.state.score}</h1>
        <button onClick={this.increaseScore}>+2</button>
        <Title>Street Fighter II Click Game</Title>
        {this.state.fighters.map(fighters => (
          <FighterCard
            id={fighters.id}
            key={fighters.id}
            name={fighters.name}
            image={fighters.image}
            onClick={FighterCard.shuffleFighters} />
        ))}
      </Wrapper>
    );
  }

}

export default App;
